var keyHeld = [false, false, false, false, false, false];
var gameActive = false;
var developer = false;
var timeout;
var prevNow;
var activeButton;

var hinted1;
var hinted2;
var remaining;
var state;
var box;
var score = 0;
var received = 0;
var identifier = 0;

var x;
var y;
var theta;
var vx;
var vy;
var omega;
var dt = 18;
const beta = 2;
const rot_drag = 440;
const force = 2;
const torque = 1200;

buttonsDisable();
popup(document.getElementById("welcome"), document.getElementById("buttonGo"));

//refresh scoreboards
scoreSend();
document.getElementById("nameField").value = "";

// game flow functions
function hideAndStart(element){
    element.setAttribute("style", "display: none");
    activeButton = undefined;
    gameStart();
    buttonsEnable();
}
function pauseUnpause(button){
    if(gameActive){
        //pause
        gameDeactivate();
        button.value = "Unpause [P]";
        button.setAttribute("class", "button green");
    }
    else{
        //unpause
        gameActivate();
        button.value = "Pause [P]";
        button.setAttribute("class", "button grey");
    }
}
function gameStart(){
    //clear old game
    var states = document.getElementById("gameSVG");
    while(states.lastChild){
        states.removeChild(states.lastChild);
    }
    score = 0;
    received = 0;
    updateScore();
    gameDeactivate();
    document.getElementById("nameSubmit").disabled = false;
    
    //set new variables
    identifier = Math.floor(Math.random() * 1e16);
    $.get({
        url: "start",
        data: {
            identifier: identifier
        },
        success: function(data){
            remaining = data;
            stateNew();
            
            //activate game
            pauseUnpause(document.getElementById("buttonPause"));
        },
        error: function(jqXHR, exception){
            handleError(jqXHR);
        }
    });
}
function gameEnd(){
    console.log("Game Over! Score: " + score);
    document.getElementById("scoreFinal").innerHTML = score;
    
    popup(document.getElementById("gameOver"), document.getElementById("buttonAgain"));
}
function gameActivate(){
    gameActive = true;
    timeout = setTimeout(update, dt);
}
function gameDeactivate(){
    gameActive = false;
    clearTimeout(timeout);
    prevNow = undefined;
}
function update(){
    // loop on itself
	// switched from using setInterval to have variable dt
	if(gameActive){
	    timeout = setTimeout(update, dt);
	}
    
	//handle physics
	var mass = box.height*box.width;
	var mom_inertia = mass*(box.width*box.width + box.height*box.height)/12;
	var acc = force / mass;
	var alpha = torque / mom_inertia;
	if(keyHeld[0]){
		vx -= acc*dt;
	}
	if(keyHeld[1]){
		vy -= acc*dt;
	}
	if(keyHeld[2]){
		vx += acc*dt;
	}
	if(keyHeld[3]){
		vy += acc*dt;
	}
	if(keyHeld[4]){
		omega += alpha*dt;
	}
	if(keyHeld[5]){
		omega -= alpha*dt;
	}
	// change position
	x += vx*dt;
	y += vy*dt;
	theta += omega*dt;
	//boundary collision
	if(x < 50){
		x = 50;
		vx = -vx;
	}
	if(x > 1550){
		x = 1550;
		vx = -vx;
	}
	if(y < 50){
		y = 50;
		vy = -vy;
	}
	if(y > 850){
		y = 850;
		vy = -vy;
	}
	// drag
	var temp = Math.min(0.99, beta / mass * dt);
	vx -= temp * vx;
	vy -= temp * vy;
	omega -= Math.min(0.99, rot_drag / mom_inertia * dt) * omega;

	// graphical update
	stateMove();
	
	// performance test
	var now = Date.now();
	if(typeof prevNow !== "undefined"){
	    var diff = now - prevNow;
	    var ratio = diff / dt;
	    //console.log(diff, dt);
	    if(dt < 100 && ratio > 2){
	        dt++;
	        updateFPS();
	    }
	    else if(dt > 16 && ratio < 1.1){
	        dt--;
	        updateFPS();
	    }
	}
	prevNow = now;
}
function updateScore(){
    document.getElementById("scoreDisplay").innerHTML = score;
}
function updateFPS(){
    document.getElementById("dt").innerHTML = dt;
    document.getElementById("fps").innerHTML = (1000/dt).toFixed(2);
}

// state functions
function stateMove(){
	// Move the states
	// Takes care of relative posisions
	var offset_x = box.x + box.width/2;
	var offset_y = box.y + box.height/2;
	state.setAttribute("transform", "translate(" + (x - offset_x) + " " + (y - offset_y) + ") rotate(" + theta + " , " + offset_x + ", " + offset_y + ")");
}
function stateNew(){
	//revert previous state
    if(typeof state !== "undefined"){
        state.setAttribute("class", null);
    }
    
    //draw state id
	var id = remaining.pop();
	
	//out of states
	if(id == null){
	    //deactivate game
	    buttonsDisable();
	    gameDeactivate();
	    return;
	}

	//spawn state
	state = document.createElementNS("http://www.w3.org/2000/svg", "path");  
	state.setAttribute("id", id);
	state.setAttribute("visibility", "hidden");
	state.setAttribute("d", state_paths[id]);
	state.setAttribute("class", "colorblue");
	document.getElementById("gameSVG").appendChild(state);
    box = state.getBBox();
	
    //reset variables
    x = 800 * Math.random() + 400; //400-1200
    y = 400 * Math.random() + 195; //195-595
    theta = 360 * Math.random();
    vx = 0.5 * Math.random() - 0.25;
    vy = 0.5 * Math.random() - 0.25;
    omega = 0.25 * Math.random() - 0.125;
    hinted1 = false;
    hinted2 = false;
	
	//make visible
	stateMove();
	state.setAttribute("visibility", "visible");
	document.getElementById("stateName").innerHTML = "???";
}
function stateAward(){
    // Look at neighbors
    // Compute distance to them
    // Compare with expected
    // Award points based on accuracy
    
    $.get({
        url: "award",
        data: {
            identifier: identifier,
            x: x,
            y: y
        },
        success: function(data){
            var neighbors = data.neighbors;
            var scores = data.scores;
            for(var i = 0; i < neighbors.length; i++){
                var neighbor = document.getElementById(neighbors[i]);
                var points = scores[i];
                score += points;
                
                //visually show good/bad placements
                stateBlink(neighbor, "color" + Math.floor(0.99999*points/10));
                spawnFloater(neighbor, "+"+points, "color"+Math.floor(0.99999*points/10));
            }
            updateScore();
            received++;
            //only end the game once all of the AJAX calls have been received
            if(received >= 50)
                setTimeout(gameEnd, 500);
        },
        error: function(jqXHR, exception){
            handleError(jqXHR);
        }
    });
}
function stateHint1(){
    // Reveal state name
    if(hinted1){
        return;
    }
    
    hinted1 = true;
    $.get({
        url: "hint1",
        data: {
            identifier: identifier
        },
        success: function(data){
            document.getElementById("stateName").innerHTML = data;
    
            spawnFloater(state, -50, "colorblue");
            score -= 50;
            updateScore();
        },
        error: function(jqXHR, exception){
            handleError(jqXHR);
        }
    });
}
function stateHint2(){
    // Highlight all bordering neighbors
    if(hinted2){
        return;
    }
    
    hinted2 = true;
    $.get({
        url: "hint2",
        data: {
            identifier: identifier
        },
        success: function(data){
            var neighbors = data;
            neighbors.forEach(function(element){
                var neighbor = document.getElementById(element);
                if(neighbor === null)
                    return;
                
                neighbor.setAttribute("class", "dinged colorpurple");
                spawnFloater(neighbor, -50, "colorpurple");
                score -= 50;
            });
            updateScore();
        },
        error: function(jqXHR, exception){
            handleError(jqXHR);
        }
    });
}
function stateBlink(s1, color){
    // Flashes the given state with the given color
    // Color must be a defined class
    // s1 is path object
    
    s1.setAttribute("class", "dinged " + color);
    setTimeout(function(){ s1.setAttribute("class", ""); }, 100);
}
function stateGetCoord(s1){
    var numbers = s1.getAttribute("transform").match(/-?\d+(.\d+)?/g).map(Number);
    if(numbers.length != 5)
        throw "Failed string extraction!";
    return [numbers[0] + numbers[3], numbers[1] + numbers[4]];
}

// other functions
function spawnFloater(s1, value, color){
    // Spawns a number above a state that quicky floats away
    // Color must be a defined class
    // s1 is path object
    
    var floater = document.createElement("p");
    floater.innerHTML = value;
    floater.setAttribute("class", "floaty " + color);
    var numbers = document.getElementById("gameDisplay");
    var states = document.getElementById("gameSVG");
    var coord = stateGetCoord(s1);
    floater.setAttribute("style", "left: " + (coord[0]/1600*states.width.baseVal.value-20) +
                         "px; top: " + (coord[1]/900*states.height.baseVal.value-45) + "px;");
    numbers.appendChild(floater);
    setTimeout(function(){ numbers.removeChild(floater); }, 2000);
}
function popup(element, button){
    element.setAttribute("style", "display: block");
    activeButton = button;
}
function buttonsDisable(){
    document.getElementById("buttonPause").disabled = true;
    document.getElementById("buttonReset").disabled = true;
    document.getElementById("buttonQuit").disabled = true;
}
function buttonsEnable(){
    document.getElementById("buttonPause").disabled = false;
    document.getElementById("buttonReset").disabled = false;
    document.getElementById("buttonQuit").disabled = false;
}
function toggleDeveloper(){
    if(developer){
        developer = false;
        document.getElementById("cheaters").setAttribute("style", "display: none");
        document.getElementById("performanceBox").setAttribute("style", "display: none");
    }
    else{
        developer = true;
        document.getElementById("cheaters").setAttribute("style", "display: block");
        document.getElementById("performanceBox").setAttribute("style", "display: block");
    }
}
function scoreSend(){
    var nameField = document.getElementById("nameField");
    if(nameField.value.length == 0){
        console.log("red");
        nameField.setAttribute("style", "border-color: red;");
        return;
    }
    nameField.setAttribute("style", "border-color: #444444;");
    document.getElementById("nameSubmit").disabled = true;
    $.get({
        url: "record",
        data: {
            identifier: identifier,
            name: nameField.value,
            score: score
        },
        success: function(data){
            for(var i = 0; i < 5; i++){
                document.getElementById("winners_name"+(i+1)).innerHTML = data.winners[i].name;
                document.getElementById("winners_score"+(i+1)).innerHTML = data.winners[i].score;
                document.getElementById("cheaters_name"+(i+1)).innerHTML = data.cheaters[i].name;
                document.getElementById("cheaters_score"+(i+1)).innerHTML = data.cheaters[i].score;
            }
        },
        error: function(jqXHR, exception){
            handleError(jqXHR);
        }
    });
}
function handleError(jqXHR){
    if(jqXHR.status == 500){
        alert('Session terminated.');
        window.location.reload();
    }
    else{
        if(confirm('Connection lost. Reload page?'))
            window.location.reload();
    }
}

// key listeners
document.onkeydown = function(e){
	var key = e.keyCode ? e.keyCode : e.which;
    //console.log(key);
    
    if(key == 13){
        // enter
        if(typeof activeButton !== "undefined"){
            activeButton.click();
        }
    }
    if(key == 192){
        // `
        toggleDeveloper();
    }
    if(key == 27){
        // esc
        document.getElementById("buttonQuit").click();
    }
    if(key == 80){
        // p
        document.getElementById("buttonPause").click();
    }
    if(key == 82){
        // r
        document.getElementById("buttonReset").click();
    }
    
    if(!gameActive)
        return;
    
	if(key == 32){
	    // space
		stateAward();
		stateNew();
	}
	if(key == 16){
	    // shift
	    stateHint1();
	}
	if(key == 67){
	    // c
	    stateHint2();
	}
	if(key == 37)
    	keyHeld[0] = true;
   	else if(key == 38)
    	keyHeld[1] = true;
	else if(key == 39)
    	keyHeld[2] = true;
	else if(key == 40)
    	keyHeld[3] = true;
	else if(key == 88)
    	keyHeld[4] = true;
	else if(key == 90)
    	keyHeld[5] = true;
}
document.onkeyup = function(e){
	var key = e.keyCode ? e.keyCode : e.which;

    if(!gameActive)
        return;
    
	if(key == 37)
    	keyHeld[0] = false;
   	else if(key == 38)
    	keyHeld[1] = false;
	else if(key == 39)
    	keyHeld[2] = false;
	else if(key == 40)
    	keyHeld[3] = false;
	else if(key == 88)
    	keyHeld[4] = false;
	else if(key == 90)
    	keyHeld[5] = false;
}